import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Search from './Search';
import Pagination from './Pagination';
import './MovieList.css';

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState('');
  const [sortBy, setSortBy] = useState('year');
  const [loading, setLoading] = useState(false); // Add loading state
  const history = useNavigate();

  useEffect(() => {
    fetchMovies();
  }, [page, genre, sortBy]);

  const fetchMovies = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5000/movies', {
        params: { genre, sortBy, page }
      });
      setMovies(response.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
      // Optionally handle error state or show error message
    } finally {
      setLoading(false);
    }
  };

  const handleMovieClick = (id) => {
    history.push(`/movies/${id}`);
  };

  return (
    <div className="movie-list-container">
      <h1>Movie List</h1>
      <Search setGenre={setGenre} setSortBy={setSortBy} />
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="movie-list">
          {movies.map(movie => (
            <div key={movie._id} className="movie-row" onClick={() => handleMovieClick(movie._id)}>
              <img src={movie.poster} alt={movie.title} className="movie-poster" />
              <div className="movie-info">
                <span className="movie-title">{movie.title} ({movie.year})</span>
                <span className="movie-genres">{movie.genres.join(' / ')}</span>
              </div>
              <div className="movie-rating">
                <span className="star">&#9733;</span> {movie.imdb.rating}
              </div>
            </div>
          ))}
        </div>
      )}
      <Pagination page={page} setPage={setPage} />
    </div>
  );
};

export default MovieList;
