import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './MovieDetail.css';

const MovieDetail = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true); // Add loading state

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/movies/${id}`);
        setMovie(response.data);
      } catch (error) {
        console.error('Error fetching movie details:', error);
        // Optionally handle error state or show error message
      } finally {
        setLoading(false);
      }
    };
    fetchMovie();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (!movie) return <div>Movie not found.</div>;

  return (
    <div className="movie-detail-container">
      <h1>{movie.title} ({movie.year})</h1>
      <p><strong>Plot:</strong> {movie.plot}</p>
      <p><strong>Cast:</strong> {movie.cast.join(', ')}</p>
      <p><strong>Directors:</strong> {movie.directors.join(', ')}</p>
      <p><strong>Runtime:</strong> {movie.runtime} minutes</p>
      <p><strong>Full Plot:</strong> {movie.fullplot}</p>
      <p><strong>Languages:</strong> {movie.languages.join(', ')}</p>
      <p><strong>Countries:</strong> {movie.countries.join(', ')}</p>
      <p><strong>Type:</strong> {movie.type}</p>
    </div>
  );
};

export default MovieDetail;
