import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Search from "./Search"
import Pagination from "./Pagination"

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState('');
  const [sortBy, setSortBy] = useState('year');

  useEffect(() => {
    fetchMovies();
  }, [page, genre, sortBy]);

  const fetchMovies = async () => {
    const response = await axios.get('http://127.0.0.1:5000/movies', {
      params: { genre, sortBy, page }
    });
    setMovies(response.data);
  };

  return (
    <div>
      <h1>Movie List</h1>
      <Search setGenre={setGenre} setSortBy={setSortBy} />
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        {movies.map(movie => (
          <li key={movie._id} style={{ margin: '20px 0' }}>
            <img src={movie.poster} alt={movie.title} style={{ width: '100px', marginRight: '20px' }} />
            {movie.title} ({movie.year}) - {movie.genres} - {movie.imdb.rating}
          </li>
        ))}
      </ul>
      <Pagination page={page} setPage={setPage} />
    </div>
  );
};

export default MovieList;
