import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Search from './Search';
import Modal from './Modal';
import Pagination from './Pagination';
import './MovieList.css'; // Import the CSS file

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState('');
  const [sortBy, setSortBy] = useState('year');
  const [rating, setRating] =useState('');
  const [title, setTitle] = useState('');

  const [openModal, setOpenModal] = useState(false);

  const handleToggleModal=()=>{
    setOpenModal(!openModal);
  }

  useEffect(() => {
    fetchMovies();
  }, [page, genre, sortBy, title]);

  const fetchMovies = async () => {
    try {
      const response = await axios.get('http://localhost:5000/movies', {
        params: { genre, sortBy, page, title }
      });
      setMovies(response.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  return (
    <div className="movie-list-container">
      <h1>Movie List</h1>
      <Search setTitle={setTitle} setSortBy={setSortBy} />
      <div className="movie-list">
        {movies.map(movie => (
          <div key={movie._id} className="movie-row">
            <img src={movie.poster} alt={movie.title} className="movie-poster" />
            <div className="movie-info">
              <span className="movie-title">{movie.title} ({movie.year})</span>
              <span className="movie-genres">{movie.genres.join(' / ')}</span>
            </div>
            <div className="movie-rating">
              <span className="star">&#9733;</span> {movie.imdb.rating}
            </div>
          </div>
        ))}
      </div>
      <Pagination page={page} setPage={setPage} />

      <Modal isVisible={openModal} onClose={handleToggleModal}>
        <h2>Modal Content</h2>
        <p>This is the content inside the modal.</p>
      </Modal>
    </div>
  );
};

export default MovieList;
