import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Search from './Search';
import Pagination from './Pagination';
import './MovieList.css'; // Import the CSS file

const MovieList = () => {
  const [movies, setMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [genre, setGenre] = useState('');
  const [sortBy, setSortBy] = useState('year');

  useEffect(() => {
    fetchMovies();
  }, [page, genre, sortBy]);

  const fetchMovies = async () => {
    try {
      const response = await axios.get('http://localhost:5000/movies', {
        params: { genre, sortBy, page }
      });
      setMovies(response.data);
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  return (
    <div className="movie-list-container">
      <h1>Movie List</h1>
      <Search setGenre={setGenre} setSortBy={setSortBy} />
      <table className="movie-table">
        <thead>
          <tr>
            <th>Title</th>
            <th>Genre</th>
            <th>Rating</th>
          </tr>
        </thead>
        <tbody>
          {movies.map(movie => (
            <tr key={movie._id}>
              <td className="movie-title">
                <img src={movie.poster} alt={movie.title} className="movie-poster" />
                <div className="movie-info">
                  <span>{movie.title} ({movie.year})</span>
                </div>
              </td>
              <td>{movie.genres}</td>
              <td className="movie-rating">
                <span className="star">&#9733;</span> {movie.imdb.rating}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Pagination page={page} setPage={setPage} />
    </div>
  );
};

export default MovieList;
